// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";

contract PodcastContract {
    using Counters for Counters.Counter;

    Counters.Counter private _podcastID;

    struct Podcast {
        string ipfsHash;
        address owner;
        uint256 amount;
        address[] supporters;
        uint totalSupport;
    }

    Podcast[] allPodcasts;
    mapping(address => uint[]) userToPodcasts;
    mapping(address => uint) public supportCount;
    mapping(uint256 => Podcast) uintToPodcast;

    event PodcastUploaded(
        uint256 indexed id,
        address indexed owner,
        string ipfsHash
    );

    event PodcastSupported(
        uint256 indexed id,
        address indexed supporter,
        uint256 indexed amount
    );

    modifier podcastExists(uint256 _id) {
        require(_id < _podcastID.current(), "Podcast ID does not exist");
        _;
    }

    modifier onlyPodcastOwner(uint256 _id) {
        require(
            uintToPodcast[_id].owner == msg.sender,
            "Only podcast owner can perform this action"
        );
        _;
    }

    //IPFS HASH SHOULD ALSO CONTAIN THE iMAGE LINK AND TITLE
    function uploadPodcast(string memory _ipfsHash, uint256 _amount) external {
        require(bytes(_ipfsHash).length > 0, "IPFS hash cannot be empty");
        require(_amount > 0, "Amount must be greater than zero");

        uint256 id = _podcastID.current();
        Podcast storage newPodcast = uintToPodcast[id];
        newPodcast.ipfsHash = _ipfsHash;
        newPodcast.amount = _amount;
        newPodcast.owner = msg.sender;

        allPodcasts.push(newPodcast);
        // uintToPodcast[id] = newPodcast;
        userToPodcasts[msg.sender].push(id);

        _podcastID.increment();

        emit PodcastUploaded(id, msg.sender, _ipfsHash);
    }

    function supportPodcast(uint256 _id) external payable podcastExists(_id) {
        Podcast storage podcast = uintToPodcast[_id];
        require(
            podcast.owner != msg.sender,
            "You cannot support your own podcast"
        );
        require(msg.value == podcast.amount, "You must send the exact amount");

        podcast.totalSupport += msg.value;
        (bool success, ) = payable(podcast.owner).call{value: msg.value}("");
        require(success, "Transfer failed.");
        supportCount[msg.sender] += 1;
        podcast.supporters.push(msg.sender);
    }

    function retrieveAllPodcasts() external view returns (Podcast[] memory) {
        return allPodcasts;
    }

    function getUserPodcasts(
        address _addr
    ) external view returns (uint[] memory) {
        return userToPodcasts[_addr];
    }

    function getPodcastInfo(uint _id) external view returns (Podcast memory) {
        return uintToPodcast[_id];
    }

    function getAllPodcast() external view returns (Podcast[] memory) {
        return allPodcasts;
    }
}

contracts/RewardsContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IPodcast.sol";
import "./interfaces/ISession.sol";

contract RewardsContract is ERC721 {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    IPodcastContract public immutable podcastContract;
    ISessions public immutable sessionsContract;
    IERC20 public immutable tokenContract;

    constructor(
        address _podcastContract,
        address _sessionsContract,
        address _verbalToken
    ) ERC721("Verbal Rewards NFT", "RNFT") {
        podcastContract = IPodcastContract(_podcastContract);
        sessionsContract = ISessions(_sessionsContract);
        tokenContract = IERC20(_verbalToken);
    }

    //keeping count of rewards
    mapping(address => uint) countOfUploadPodcastRewards;
    mapping(address => uint) countOfSupportRewards;
    mapping(address => uint) countOfAttendanceRewards;
    mapping(address => uint) countOfMentoringRewards;

    function checkAndReward(address _user) external {
        // Check if the user uploaded a podcast
        uint[] memory usersPodcasts = podcastContract.getUserPodcasts(_user);
        if (
            countOfUploadPodcastRewards[_user] != 0 &&
            countOfUploadPodcastRewards[_user] % 30 == 0
        ) {
            //mint NFT for every 30 uploads
            _mintNFT(_user);
        }
        if (usersPodcasts.length > countOfUploadPodcastRewards[_user]) {
            //award 1 token for every podcast  upload
            uint tokensEarned = usersPodcasts.length -
                countOfUploadPodcastRewards[_user];
            countOfUploadPodcastRewards[_user] += tokensEarned;
            tokenContract.transfer(_user, tokensEarned);
            return;
        }

        // Check if the user supported a podcast
        uint userSupportCount = podcastContract.supportCount(_user);
        if (
            countOfSupportRewards[_user] != 0 &&
            countOfSupportRewards[_user] % 20 == 0
        ) {
            //mint NFT for every 20 supports
            _mintNFT(_user);
        }
        if (userSupportCount > countOfSupportRewards[_user]) {
            //award 2 token for every support
            uint tokensEarned = userSupportCount - countOfSupportRewards[_user];
            countOfSupportRewards[_user] += ((tokensEarned) * 2);
            tokenContract.transfer(_user, tokensEarned * 2);
            return;
        }

        // Check if the user attended a session
        uint userAttendance = sessionsContract.sessionsAttendedCount(_user);
        if (
            countOfAttendanceRewards[_user] != 0 &&
            countOfAttendanceRewards[_user] % 10 == 0
        ) {
            //mint NFT for every 10 attendance
            _mintNFT(_user);
        }
        if (userAttendance > countOfAttendanceRewards[_user]) {
            //award 1 token for every attendance
            uint tokensEarned = userAttendance -
                countOfAttendanceRewards[_user];
            countOfAttendanceRewards[_user] += tokensEarned;
            tokenContract.transfer(_user, tokensEarned);
            return;
        }

        // Check if the user mentored a session
        uint userMentoringCount = sessionsContract.sessionsMentoredCount(_user);
        if (
            countOfMentoringRewards[_user] != 0 &&
            countOfMentoringRewards[_user] % 12 == 0
        ) {
            //mint NFT for every 12 mentoring
            _mintNFT(_user);
        }
        if (userMentoringCount > countOfMentoringRewards[_user]) {
            //award 2 token for every mentoring
            uint tokensEarned = userMentoringCount -
                countOfMentoringRewards[_user];
            countOfMentoringRewards[_user] += ((tokensEarned) * 2);
            tokenContract.transfer(_user, tokensEarned * 2);
            return;
        }
    }

    function _mintNFT(address _user) internal {
        _tokenIds.increment();
        uint256 tokenId = _tokenIds.current();
        _mint(_user, tokenId);
    }

    function getCountOfUploadPodcastRewards(
        address userAddress
    ) external view returns (uint) {
        return countOfUploadPodcastRewards[userAddress];
    }

    function getCountOfSupportRewards(
        address userAddress
    ) external view returns (uint) {
        return countOfSupportRewards[userAddress];
    }

    function getCountOfAttendanceRewards(
        address userAddress
    ) external view returns (uint) {
        return countOfAttendanceRewards[userAddress];
    }

    function getCountOfMentoringRewards(
        address userAddress
    ) external view returns (uint) {
        return countOfMentoringRewards[userAddress];
    }
}

contracts/Sessions.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";

contract Sessions {
    using Counters for Counters.Counter;

    Counters.Counter private _sessionID;

    struct Session {
        address mentor;
        address student;
        bool isAccepted;
        uint256 timeStamp;
        string meetingLink;
        uint256 paymentFee;
        uint256 sessionId;
        string topic;
    }

    event SessionScheduled(
        address indexed mentor,
        address indexed student,
        uint256 fee,
        string meetingLink,
        uint256 indexed id
    );
    event SessionCancelled(uint256 indexed sessionId);
    event SessionAccepted(uint256 indexed sessionId);

    Session[] public allSessions;
    //mapping an address to all Id of it sessions
    mapping(address => uint[]) public addressToSessions;
    mapping(uint256 => Session) public uintToSession;

    mapping(address => uint) public sessionsAttendedCount;
    mapping(address => uint) public sessionsMentoredCount;

    mapping(address => uint) mentorsPrice;

    modifier isTimeFrameAlreadyTaken(uint256 _timeStamp) {
        require(!isTimeFrameTaken(_timeStamp), "Timeframe is already taken");
        _;
    }

    function isTimeFrameTaken(uint256 _timeStamp) public view returns (bool) {
        for (uint256 i = 0; i < allSessions.length; i++) {
            if (allSessions[i].timeStamp == _timeStamp) {
                return true;
            }
        }
        return false;
    }

    function scheduleASession(
        address _mentor,
        uint256 _timestamp,
        string memory _meetingLink,
        uint256 _paymentFee,
        string memory _topic
    ) external payable isTimeFrameAlreadyTaken(_timestamp) {
        require(fetchMentorsPrice(_mentor) != 0, "Invalid Mentor");
        require(msg.value >= fetchMentorsPrice(_mentor), "Cant pay mentor");

        uint256 id = _sessionID.current();
        Session storage newSession = uintToSession[id];
        newSession.mentor = _mentor;
        newSession.student = msg.sender;
        newSession.meetingLink = _meetingLink;
        newSession.timeStamp = _timestamp;
        newSession.sessionId = id;
        newSession.paymentFee = _paymentFee;
        newSession.topic = _topic;
        allSessions.push(newSession);
        addressToSessions[msg.sender].push(id);
        uintToSession[id] = newSession;
        _sessionID.increment();

        emit SessionScheduled(
            _mentor,
            msg.sender,
            newSession.paymentFee,
            _meetingLink,
            id
        );
    }

    function cancelSession(uint256 _sessionId) external {
        Session storage session = uintToSession[_sessionId];
        require(
            session.student == msg.sender || session.mentor == msg.sender,
            "You are not authorized to make changes to this session"
        );
        if (msg.sender == session.mentor) {
            session.isAccepted = false;
            // addressToSessions[session.student].isAccepted = true;
        } else {
            (bool success, ) = payable(session.mentor).call{value: fetchMentorsPrice(session.mentor)}("");
            require(success, "Transfer failed.");
            delete addressToSessions[msg.sender][_sessionId];
            delete uintToSession[_sessionId];
        }

        emit SessionCancelled(_sessionId);
    }

    function getAllSession() external view returns (Session[] memory) {
        return allSessions;
    }

    function acceptSession(uint256 _sessionId) external {
        Session storage session = uintToSession[_sessionId];
        require(
            session.mentor == msg.sender,
            "You are not the mentor of this session"
        );
        require(session.isAccepted == false, "Already accepted");
        session.isAccepted = true;
          (bool success, ) = payable(session.mentor).call{value: fetchMentorsPrice(session.mentor)}("");
            require(success, "Transfer failed.");
        sessionsMentoredCount[session.mentor] += 1;
        sessionsAttendedCount[session.student] += 1;

        // addressToSessions[session.student].isAccepted = true;
        emit SessionAccepted(_sessionId);
    }

    function registerMentorPrice(uint _amount) external {
        require(_amount != 0, "CAnt set zero amount");
        mentorsPrice[msg.sender] = _amount;
    }

    function fetchMentorsPrice(address _addr) public view returns (uint) {
        return mentorsPrice[_addr];
    }

    function getUserSessions(
        address _userAddress
    ) external view returns (uint[] memory) {
        return addressToSessions[_userAddress];
    }

    function getSessionDetails(
        uint _sessionId
    ) external view returns (Session memory) {
        return uintToSession[_sessionId];
    }
}

contracts/VerbalToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract VerbalToken is ERC20 {
    address public Holder;
    address public immutable deployer;

    bool called;

    constructor() ERC20("VerbalToken", "VTK") {
        deployer = msg.sender;
    }

    function initFunction(address holder) external {
        require(msg.sender == deployer && called == false);
        Holder = holder;
        _mint(holder, 100000 * 10 ** decimals());
        called = true;
    }
}

